<?php

namespace AppBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query\ResultSetMapping;

/**
 * GameRepository
 *
 * This class was generated by the PhpStorm "Php Annotations" Plugin. Add your own custom
 * repository methods below.
 */
class GameRepository extends EntityRepository
{

    /**
     * @param Player $p1
     * @param Player $p2
     * @return \Doctrine\ORM\QueryBuilder
     */
    public function findByPlayers(Player $p1, Player $p2)
    {
        $builder = $this->createQueryBuilder('game');
        $builder->where(
            $builder->expr()->orX(
                $builder->expr()->eq('game.player1', $p1->getId()),
                $builder->expr()->eq('game.player2', $p1->getId())
            ),
            $builder->expr()->orX(
                $builder->expr()->eq('game.player1', $p2->getId()),
                $builder->expr()->eq('game.player2', $p2->getId())
            )
        );
        return $builder;
    }

    /**
     * @return array
     */
    public function findLeastGamesByPlayer()
    {
        $results = [];
        $this->findByPlayerGames(1, $results);
        $this->findByPlayerGames(2, $results);
        asort($results);

        $value = reset($results);
        $key = key($results);

        return ['player' => $key, 'numgames' => $value];
    }

    /**
     * @return array
     */
    public function findMostGamesByPlayer()
    {
        $results = [];
        $this->findByPlayerGames(1, $results);
        $this->findByPlayerGames(2, $results);
        arsort($results);

        $value = reset($results);
        $key = key($results);

        return ['player' => $key, 'numgames' => $value];
    }

    private function findByPlayerGames($player = 1, &$output = array())
    {
        if ($player === 1) {
            $sql = 'SELECT COUNT(id), player1_id as id FROM game GROUP BY player1_id ORDER BY COUNT(id) DESC';
        } else {
            $sql = 'SELECT COUNT(id), player2_id as id FROM game GROUP BY player2_id ORDER BY COUNT(id) DESC';
        }

        $rsm = new ResultSetMapping();
        $rsm->addScalarResult('COUNT(id)', 'numgames', 'integer');
        $rsm->addScalarResult('id', 'id', 'integer');
        $q = $this->_em->createNativeQuery($sql, $rsm);
        $results = $q->getResult();
        foreach($results as $res) {
            if (array_key_exists($res['id'], $output)) {
                $output[$res['id']] += $res['numgames'];
            } else {
                $output[$res['id']] = $res['numgames'];
            }
        }
    }

    public function getManager()
    {
        return $this->_em;
    }

    /**
     * @param int $playerId
     * @return \Doctrine\ORM\QueryBuilder
     */
    public function findByPlayer($playerId)
    {
        $builder = $this->createQueryBuilder('game');
        $builder->where(
            $builder->expr()->orX(
                $builder->expr()->eq('game.player1', $playerId),
                $builder->expr()->eq('game.player2', $playerId)
            )
        );
        return $builder;
    }

    /**
     * @param int $playerId
     * @return \Doctrine\ORM\QueryBuilder
     */
    public function findWonGamesByPlayer($playerId)
    {
        $builder = $this->createQueryBuilder('game');
        $builder->where(
            $builder->expr()->orX(
                $builder->expr()->andX(
                    $builder->expr()->eq('game.player1', $playerId),
                    $builder->expr()->eq('game.winner', Game::P1WINNER)
                ),
                $builder->expr()->andX(
                    $builder->expr()->eq('game.player2', $playerId),
                    $builder->expr()->eq('game.winner', Game::P2WINNER)
                )
            )
        );
        return $builder;
    }

    /**
     * @param int $playerId
     * @return \Doctrine\ORM\QueryBuilder
     */
    public function findLostGamesByPlayer($playerId)
    {
        $builder = $this->createQueryBuilder('game');
        $builder->where(
            $builder->expr()->orX(
                $builder->expr()->andX(
                    $builder->expr()->eq('game.player1', $playerId),
                    $builder->expr()->eq('game.winner', Game::P2WINNER)
                ),
                $builder->expr()->andX(
                    $builder->expr()->eq('game.player2', $playerId),
                    $builder->expr()->eq('game.winner', Game::P1WINNER)
                )
            )
        );
        return $builder;
    }

    /**
     * @param Player $player
     * @return array
     */
    public function getGamesPrDay(Player $player)
    {
        $rsm = new ResultSetMapping();
        $rsm->addScalarResult('counter', 'counter', 'integer');
        $rsm->addScalarResult('date', 'date', 'datetime');
        $sql = 'SELECT COUNT(*) as counter, date FROM game WHERE player1_id = :p1 OR player2_id = :p2 GROUP BY DAY(date)';
        $nq = $this->_em->createNativeQuery($sql, $rsm);
        $nq->setCacheable(true);
        $nq->setResultCacheLifetime(30);
        $nq->setResultCacheId(sprintf('games_pr_day_%d', $player->getId()));
        $nq->setParameter(':p1', $player->getId(), 'integer');
        $nq->setParameter(':p2', $player->getId(), 'integer');
        return $nq->execute();
    }

}
